var s_count = $('.slider .item').length;
$('.slider .item').eq(0).addClass('current').each(function() {});
$('.slider').append('<div class="next-prev"><div class="prev" onclick="showSlide(' + (s_count - 1) + ');"><img src="' + $('.slider .item').eq(s_count - 1).find('img').attr('src') + '" /></div><div class="next" onclick="showSlide(1);"><img src="' + $('.slider .item').eq(1).find('img').attr('src') + '" /></div></div>');
   
function showSlide(i) {
	var s_count = $('.slider .item').length,
	 oheight = $('.slider .item.current').height();

	if (i < 0) i = s_count - 1;
	if (i > s_count - 1) i = 0;

	if (i == s_count - 1) {
	 prev = i - 1;
	 next = 0;
	} else if (i == 0) {
	 prev = s_count - 1;
	 next = i + 1;
	} else {
	 prev = i - 1;
	 next = i + 1;
	}

	$('.slider .item').removeClass('current');
	$('.slider .item').eq(i).addClass('current hidden').each(function() {
	 var nheight = $(this).outerHeight(true);
	 $(this).css({
	   height: oheight
	 }).removeClass('hidden').animate({
	   height: nheight
	 }, 'fast');
	 $('.slider .next-prev img').eq(0).fadeOut('fast', function() {
	   $(this).attr({
		 'src': $('.slider .item').eq(prev).find('img').attr('src')
	   }).fadeIn('fast').parent('.prev').attr({
		 'onclick': 'showSlide(' + prev + ')'
	   });
	 });
	 $('.slider .next-prev img').eq(1).fadeOut('fast', function() {
	   $(this).attr({
		 'src': $('.slider .item').eq(next).find('img').attr('src')
	   }).fadeIn('fast').parent('.next').attr({
		 'onclick': 'showSlide(' + next + ')'
	   })
	 });

	});
}

