/* Таймер обратного отсчета для календаря
/*******/
function episodeTimer(){
	$(".episode-countdown time").each(function(indx, e){
		var timestamp = new Date($(this).attr('data-time'));
		timestamp = timestamp.valueOf();
		timestamp = (timestamp-(new Date().getTime()))/1000;
		
		var id = 'id_'+timestamp;
		if( timestamp > 0 ) {
			id = setInterval(function(){
				timestamp = timestamp - 1;

				if (timestamp < 0) timestamp = 0;
			 
				var day = Math.floor( (timestamp/60/60) / 24);
				var hour = Math.floor(timestamp/60/60);
				var mins = Math.floor((timestamp - hour*60*60)/60);
				var secs = Math.floor(timestamp - hour*60*60 - mins*60); 
				var left_hour = Math.floor( (timestamp - day*24*60*60) / 60 / 60 );
			 
				if(String(mins).length < 2)
					mins = "0" + mins;
				
				if(String(secs).length < 2)
					secs = "0" + secs;
				
				$(e).text(day+'д '+left_hour+'ч '+mins+'м '+secs);
				
				if(timestamp <= 0){
					$(e).text('Уже вышла');
					clearInterval(id)
				}
			}, 1000);
		}
	});
     
}